import { redirect } from "react-router-dom";

export const getTokenDuration = () => {
  const storedExperiationDate = localStorage.getItem("experiation");
  const experiationDate = new Date(storedExperiationDate);
  const now = new Date();
  const duration = experiationDate.getTime() - now.getTime();
  return duration;
};

export const getAuthToken = () => {
  const token = localStorage.getItem("token");

  const tokenDuration = getTokenDuration();

  if (tokenDuration < 0) {
    return "EXPIRED";
  }

  return token;
};

export const tokenLoader = () => {
  return getAuthToken();
};

export const checkAuthLoader = () => {
  const token = getAuthToken();

  if (!token) {
    return redirect("/auth");
  }

  return null;
};
